# -------------------------------------------------------
# 2. 工具函数
# -------------------------------------------------------

def random_gender():
    return random.choice(["male", "female"])

def random_age():
    return random.randint(19, 23)

def generate_gpa():
    """
    正态分布(均值 3.55, 标准差 0.25)，范围裁剪在 [3.1, 4.0]
    """
    g = np.random.normal(3.55, 0.25)
    return round(min(4.0, max(3.1, g)), 2)

def get_major(user_id):
    """
    user_id=61~660 → 分四个专业区段
    1~100     → 智能科学与技术
    101~200   → 电子信息工程
    201~400   → 物联网工程
    401~600   → 电信工程及管理
    """
    idx = user_id - 60  # 1~600

    if 1 <= idx <= 100:
        return "智能科学与技术"
    elif 101 <= idx <= 200:
        return "电子信息工程"
    elif 201 <= idx <= 400:
        return "物联网工程"
    elif 401 <= idx <= 600:
        return "电信工程及管理"


def get_class_name(index):
    """
    每 25 人一个班，班级名称为 20232151xx
    xx 从 01 到 24 （600/25=24班）
    index 为 1~600
    """
    class_no = (index - 1) // 25 + 1  # 1~24
    return f"20232151{class_no:02d}"


# -------------------------------------------------------
# 3. 生成 SQL 文件
# -------------------------------------------------------

output = []

for i in range(600):
    name = names[i]
    user_id = 61 + i                       # 61~660
    student_id = 2023213000 + (i + 1)      # 2023213xxx，其中 xxx = (user_id-60)
    age = random_age()
    gender = random_gender()
    major = get_major(user_id)
    grade = 3
    class_name = get_class_name(i + 1)
    phone = "19807312088"
    email = f"{student_id}@bupt.edu.cn"
    gpa = generate_gpa()

    sql = f"({student_id}, {user_id}, '{name}', '{gender}', {age}, '{major}', {grade}, '{class_name}', '{phone}', '{email}', {gpa})"
    output.append(sql)


# -------------------------------------------------------
# 4. 写入文件
# -------------------------------------------------------

with open("students_insert.sql", "w", encoding="utf-8") as f:
    f.write("INSERT INTO students (student_id, user_id, name, gender, age, major, grade, class_name, phone, email, gpa) VALUES\n")
    f.write(",\n".join(output))
    f.write(";\n")

print("🎉 已生成 students_insert.sql 文件，共 600 条记录！")
